sys_variable_value.value:
// for each completed task get value of score field

var gr = new GlideRecord('x_gfnll_reviews_management_questions');
gr.addQuery('review',current.sys_id);
gr.addEncodedQuery('active!=true');
gr.query();

var totalScore = 0;
var taskCount = 0;
var avgScore = 0;
while(gr.next()){
	totalScore += parseInt(gr.score,10);
	taskCount++;
}

// calculate all scores and / by number of tasks to get avg score
if(taskCount>0){
	avgScore = totalScore/taskCount;
}

// copy avg score to current review record
current.assessment_score = avgScore;

