// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
//   answer = ifScript();
//
//   function ifScript() {
//      if (condition is true) {
//         return 'yes';
//      }
//      return 'no';
//   }
var ga = new GlideAggregate('x_gfnll_reviews_management_questions');
ga.addQuery('review',current.sys_id);
ga.addEncodedQuery('active=true');
ga.addAggregate('COUNT');
ga.query();
var tasks = 0;
if(ga.next()){
	tasks = ga.getAggregate('COUNT');
}
if(tasks>0){
	answer = 'no';
} else {
	answer = 'yes';
}

