var list = current.reviewer_s.toString();
gs.info("List: " + list);
var listArr = list.split(',');
gs.info("List array: " + listArr);


var i;
for(i=0; i < listArr.length; i++){
	var gr = new GlideRecord('x_gfnll_reviews_management_questions');
	gr.initialize();
	gr.review = current.sys_id;
	gr.assigned_to = listArr[i];
	gr.short_description = 'New management assessment for ' + current.assigned_to.getDisplayValue();
	gr.insert();
}

